mapper.insert:
INSERT INTO BLOG_MENU   (
                            INTG_MBR_NO
                          , BLG_MNU_NO
                          , BLG_MNU_NM
                          , RPRS_MNU_YN
                          , NCS_YN
                          , USE_YN
                          , BLG_MNU_TY_CD
                          , PAG_PST_CNT
                          , PRLG_FRM_CD
                          , NTF_YN
                          , SEQ
                          , REG_NO
                          , REG_DTT
                          , MOD_NO
                          , MOD_DTT
                        ) 
SELECT 
        #{intgMbrNo}
      , #{blgMnuNo}
      , #{blgMnuNm}
      , #{rprsMnuYn}
      , #{ncsYn}
      , #{useYn}
      , #{blgMnuTyCd}
      , ${pagPstCnt}
      , #{prlgFrmCd}
      , #{ntfYn}
      , (SELECT IFNULL(MAX(SEQ) + 1, 1) FROM BLOG_MENU WHERE INTG_MBR_NO = #{intgMbrNo})
      , #{regNo}
      , NOW()
      , #{regNo}
      , NOW()

INSERT INTO BLOG_INFO   (
                            INTG_MBR_NO
                          , BLG_MNU_NO
                          , BLG_NM
                          , BLG_INTRO
                          , TPC_CTG_NO
                        ) 
VALUES                  (
                            #{intgMbrNo}
                          , #{blgMnuNo}
                          , #{blgNm}
                          , NULL
                          , #{tpcCtgNo}
                        );
